import React,{Component} from 'react';
import './App.css';
import Car from './Car/Car'

class App extends Component {

  state = {
    cars : [
        {name: 'Ford', year: "2010"},
        {name: 'Mazda', year: "2007"},
        {name: 'Volvo', year: "2001"}
    ],
      pageTitle: 'React components',
      showCars: false
  };
    changeTitleHandler = pageTitle =>{
        this.setState({
            pageTitle
        })
    }

    toggleCarsHandler = () =>{
        this.setState({
           showCars: !this.state.showCars
        });
    };


  render(){
    const divStyle = {
      'textAlign' : 'center',
    };

    let cars = null;

    if(this.state.showCars){
        cars = this.state.cars.map((car)=>{
            return (
                <Car
                    key={car.name}
                    name={car.name}
                    year={car.year}
                    onChangeTitle={()=>
                        this.changeTitleHandler(car.name)}
                />
            )
        })
    }


    return(
        <div style={divStyle}>
          <h1>{this.state.pageTitle}</h1>


          <button onClick={this.toggleCarsHandler}>
              Toggle cars</button>
            {cars}
        </div>

    )

  }
}

export default App;
